    const {demoRedux}=this.props
    return (
    // console.log(this.props)
      
      <div className='container'>
       <p className="display-3">Number: {demoRedux}</p>
       <button className='btn btn-success mr-5'>+</button>
       <button className='btn btn-danger'>-</button>
      </div>
    )
  }
}
