import React, { Component } from "react";
import phones from "./data.json";
import Phone from "./Phone";
export default class BTPhone extends Component {
    state={
        phoneDefault:{
            "maSP": 1, "tenSP": "VinSmart Live", "manHinh": "AMOLED, 6.2, Full HD+", "heDieuHanh": "Android 9.0 (Pie)", "cameraTruoc": "20 MP", "cameraSau": "Chính 48 MP & Phụ 8 MP, 5 MP", "ram": "4 GB", "rom": "64 GB", "giaBan": 5700000, "hinhAnh": "./image/phones/vsphone.jpg"
        }
    }
    handlePhoneDefault=(phone)=>{
      this.setState({
        phoneDefault:phone
      })
    }
  render() {
    return (
      <div>
        <div className="row">
          {phones.map((phone) => {
            return (
              <div className="col-4" key={phone.maSP}>
                <Phone product={phone}></Phone>
              </div>
            );
          })}
        </div>
       <div className="row">
        <div className="col-4"><img src={this.state.phoneDefault.hinhAnh} alt="" className="img-fluid"/></div>
        <div className="col-8">
            <h1>Thông số kỹ thuật</h1>
            <table className="table">
                <thead></thead>
                <tbody>
                    <tr>
                        <td>Màn hình</td>
                        <td>{this.state.phoneDefault.manHinh}</td>
                    </tr>
                    <tr>
                        <td>Hệ điều hành</td>
                        <td>{this.state.phoneDefault.heDieuHanh}</td>
                    </tr>
                    <tr>
                        <td>Camera trước</td>
                        <td>{this.state.phoneDefault.cameraTruoc}</td>
                    </tr>
                    <tr>
                        <td>Camera sau</td>
                        <td>{this.state.phoneDefault.cameraSau}</td>
                    </tr>
                    <tr>
                        <td>Ram</td>
                        <td>{this.state.phoneDefault.ram}</td>
                    </tr>
                    <tr>
                        <td>Rom</td>
                        <td>{this.state.phoneDefault.rom}</td>
                    </tr>
                </tbody>
            </table>
        </div>
       </div>
      </div>
    );
  }
}
