import React, { Component } from "react";
import phones from "./data.json";
export default class BTPhone extends Component {
    state={
        phoneDefault:{
            "maSP": 1, "tenSP": "VinSmart Live", "manHinh": "AMOLED, 6.2, Full HD+", "heDieuHanh": "Android 9.0 (Pie)", "cameraTruoc": "20 MP", "cameraSau": "Chính 48 MP & Phụ 8 MP, 5 MP", "ram": "4 GB", "rom": "64 GB", "giaBan": 5700000, "hinhAnh": "./image/phones/vsphone.jpg"
        }
    }
  render() {
    return (
      <div>
        <div className="row">
          {phones.map((phone) => {
            return (
              <div className="col-4" key={phone.maSP}>
                <div className="card">
                  <img src={phone.hinhAnh} alt={phone.tenSP} />
                  <div className="card-body">
                    <h3>{phone.tenSP}</h3>
                    <button className="btn btn-success">Xem chi tiết</button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
       .row>.col-4>img^.col
      </div>
    );
  }
}
