import React, { Component } from "react";
import ChiTietSP from "./ChiTietSP";
import phones from "./data.json";
import GioHang from "./GioHang";
import Phone from "./Phone";
export default class BTPhone extends Component {
    state={
        phoneDefault:{
            "maSP": 1, "tenSP": "VinSmart Live", "manHinh": "AMOLED, 6.2, Full HD+", "heDieuHanh": "Android 9.0 (Pie)", "cameraTruoc": "20 MP", "cameraSau": "Chính 48 MP & Phụ 8 MP, 5 MP", "ram": "4 GB", "rom": "64 GB", "giaBan": 5700000, "hinhAnh": "./image/phones/vsphone.jpg"
        },
        cart:[
          { "maSP": 1, "tenSP": "VinSmart Live", "manHinh": "AMOLED, 6.2, Full HD+", "heDieuHanh": "Android 9.0 (Pie)", "cameraTruoc": "20 MP", "cameraSau": "Chính 48 MP & Phụ 8 MP, 5 MP", "ram": "4 GB", "rom": "64 GB", "giaBan": 5700000, "hinhAnh": "./image/phones/vsphone.jpg", "soLuong":"1" }
        ]
    }
    handlePhoneDefault=(phone)=>{
      this.setState({
        phoneDefault:phone
      })
    }
    handleCart=(phoneClick)=>{
      //spread operator
      const value={...phoneClick,soLuong:1}
      const data=[...this.state.cart]
      const index=data.findIndex((phone)=>phone.maSP===phoneClick.maSP)
      if(index!==-1){
        value.soLuong+=1
      }else{
        
      }
      data.push(value)
      //set lại state thêm vào state sản phẩm đc click
      this.setState({
        cart:data,
      })
    }
  render() {
    
    return (
      <div>
        <GioHang cart={this.state.cart}></GioHang>
        <div className="row">
          {phones.map((phone) => {
            return (
              <div className="col-4" key={phone.maSP}>
                <Phone product={phone} handlePhoneDefault={this.handlePhoneDefault} handleCart={this.handleCart}></Phone>
              </div>
            );
          })}
        </div>
       <ChiTietSP phone={this.state.phoneDefault}></ChiTietSP>
      </div>
    );
  }
}
